import { useEffect, useState } from "react";
import API from "../api";

function DoctorDashboard() {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await API.get("/appointments", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      });
      setAppointments(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  const styles = {
    container: {
      minHeight: "100vh",
      backgroundColor: "#0a0a0a",
      padding: "40px 20px",
      fontFamily: "'Inter', sans-serif",
      color: "#ffffff",
    },
    wrapper: {
      maxWidth: "900px",
      margin: "0 auto",
    },
    header: {
      borderBottom: "1px solid #222",
      paddingBottom: "20px",
      marginBottom: "30px",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
    },
    title: {
      fontSize: "28px",
      fontWeight: "700",
      letterSpacing: "-0.5px",
    },
    appointmentRow: {
      backgroundColor: "#111",
      border: "1px solid #222",
      borderRadius: "10px",
      padding: "20px",
      marginBottom: "12px",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      transition: "background-color 0.2s ease",
    },
    dateContainer: {
      display: "flex",
      flexDirection: "column",
    },
    dateLabel: {
      fontSize: "12px",
      color: "#666",
      textTransform: "uppercase",
      letterSpacing: "1px",
      marginBottom: "4px",
    },
    dateValue: {
      fontSize: "16px",
      fontWeight: "500",
    },
    statusBadge: (status) => ({
      padding: "6px 14px",
      borderRadius: "20px",
      fontSize: "12px",
      fontWeight: "600",
      textTransform: "capitalize",
      backgroundColor: status === "confirmed" ? "#ffffff" : "#222",
      color: status === "confirmed" ? "#000000" : "#888",
      border: status === "confirmed" ? "none" : "1px solid #333",
    }),
    emptyState: {
      textAlign: "center",
      padding: "100px 0",
      color: "#444",
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.wrapper}>
        <header style={styles.header}>
          <h2 style={styles.title}>Doctor Dashboard</h2>
          <span style={{ color: "#666", fontSize: "14px" }}>
            {appointments.length} Appointments Total
          </span>
        </header>

        {appointments.length > 0 ? (
          appointments.map((a, index) => (
            <div 
              key={index} 
              style={styles.appointmentRow}
              onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#161616")}
              onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#111")}
            >
              <div style={styles.dateContainer}>
                <span style={styles.dateLabel}>Scheduled Date</span>
                <span style={styles.dateValue}>{a.date}</span>
              </div>

              <div style={styles.statusBadge(a.status?.toLowerCase())}>
                {a.status || "Pending"}
              </div>
            </div>
          ))
        ) : (
          <div style={styles.emptyState}>
            <p>No appointments found.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default DoctorDashboard;
