import { useEffect, useState } from "react";
import API from "../api";
import { Link } from "react-router-dom";

function Dashboard() {
  const [doctors, setDoctors] = useState([]);

  useEffect(() => {
    API.get("/doctors").then((res) => setDoctors(res.data));
  }, []);

  const styles = {
    container: {
      minHeight: "100vh",
      backgroundColor: "#0a0a0a", // Deep obsidian
      padding: "60px 20px",
      fontFamily: "'Inter', sans-serif",
      color: "#ffffff",
    },
    header: {
      textAlign: "center",
      marginBottom: "50px",
    },
    title: {
      fontSize: "32px",
      fontWeight: "700",
      letterSpacing: "-1px",
      margin: "0 0 10px 0",
    },
    subtitle: {
      color: "#666",
      fontSize: "16px",
    },
    grid: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
      gap: "24px",
      maxWidth: "1200px",
      margin: "0 auto",
    },
    card: {
      backgroundColor: "#161616",
      border: "1px solid #2a2a2a",
      borderRadius: "12px",
      padding: "24px",
      transition: "transform 0.2s ease, border-color 0.2s ease",
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
    },
    docName: {
      fontSize: "20px",
      fontWeight: "600",
      marginBottom: "8px",
      color: "#fff",
    },
    specialty: {
      fontSize: "14px",
      color: "#a0a0a0", // Dimmed text for hierarchy
      marginBottom: "24px",
      textTransform: "uppercase",
      letterSpacing: "1px",
    },
    link: {
      display: "inline-block",
      textAlign: "center",
      textDecoration: "none",
      backgroundColor: "#ffffff",
      color: "#000000",
      padding: "12px",
      borderRadius: "8px",
      fontWeight: "600",
      fontSize: "14px",
      transition: "opacity 0.2s ease",
    },
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2 style={styles.title}>Available Specialists</h2>
        <p style={styles.subtitle}>Select a professional to schedule your consultation</p>
      </header>

      <div style={styles.grid}>
        {doctors.map((doc) => (
          <div 
            key={doc.id} 
            style={styles.card}
            onMouseOver={(e) => (e.currentTarget.style.borderColor = "#444")}
            onMouseOut={(e) => (e.currentTarget.style.borderColor = "#2a2a2a")}
          >
            <div>
              <h3 style={styles.docName}>{doc.name}</h3>
              <p style={styles.specialty}>{doc.specialty}</p>
            </div>
            
            <Link 
              to={`/book/${doc.id}`} 
              style={styles.link}
              onMouseOver={(e) => (e.style.opacity = "0.85")}
              onMouseOut={(e) => (e.style.opacity = "1")}
            >
              Book Now
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
